import Counter from "./Counter.vue";
import CounterSum from "./CounterSum.vue";
export default {
  name: 'counter-group',
  components: {
    Counter: Counter,
    CounterSum: CounterSum
  },
  props: {
    counterNumber: Number
  },
  created: function created() {
    // 根据 counterNumber 生成 counter 数据和组件
    for (var i = 0; i < this.counterNumber; i++) {
      this.$store.state.counters.push({
        value: 0
      });
    }
  },
  methods: {
    handleValueChange: function handleValueChange(index, value) {
      this.$store.state.counters[index].value = value;
    }
  }
};
