export default {
  name: "CheckboxComponent",
  computed: {
    filterTodoList: function filterTodoList() {
      var filterList = [];

      for (var index = 0; index < this.$store.state.todoList.length; index++) {
        var element = this.$store.state.todoList[index];

        if (this.$store.state.currentFilter === "all" || this.$store.state.currentFilter === element.status) {
          filterList.push({
            content: element.content,
            status: element.status,
            id: element.id
          });
        }
      }

      return filterList;
    }
  },
  methods: {
    changeStatus: function changeStatus(item) {
      var status = item.status === "active" ? "completed" : "active";
      var index = parseInt(item.id);
      this.$store.state.todoList[index].status = status;
    }
  }
};
